create policy "Anyone can insert scores"
on "public"."leaderboard"
as permissive
for insert
to public
with check (true);


create policy "Anyone can insert to leaderboard"
on "public"."leaderboard"
as permissive
for insert
to public
with check (true);


create policy "Anyone can update scores"
on "public"."leaderboard"
as permissive
for update
to public
using (true);


create policy "Anyone can view leaderboard"
on "public"."leaderboard"
as permissive
for select
to public
using (true);


create policy "Anyone can view the leaderboard"
on "public"."leaderboard"
as permissive
for select
to public
using (true);


create policy "allow_all_users_insert_private_notes"
on "public"."notes"
as permissive
for insert
to public
with check ((public = false));


create policy "allow_all_users_select_public_notes"
on "public"."notes"
as permissive
for select
to public
using ((public = true));



